import React from "react";
import styled, { css } from "styled-components";
import Heading from "./headings";
import Paragraph from "./paragraph";
import { update } from "./BooksAPI";

const BookOptions = ({ book, onUpdate }) => {
  console.log(book);

  return (
    <Options className="bookOptions" style={bookOptions}>
      <Heading size="small">Move to:</Heading>
      {book.shelf != "wantToRead" && (
        <button
          onClick={() => {
            console.log("Test", book);
            update(book, "wantToRead").then((b) => onUpdate(b));
          }}
        >
          Want to read
        </button>
      )}
      {book.shelf != "read" && (
        <button
          onClick={() => {
            console.log("Test", book);
            update(book, "read").then((b) => onUpdate(b));
          }}
        >
          Read
        </button>
      )}
      {book.shelf != "currentlyReading" && (
        <button
          onClick={() => {
            console.log("Test", book);
            update(book, "currentlyReading").then((b) => onUpdate(b));
          }}
        >
          Currently Reading
        </button>
      )}
      <button>Remove from your lists</button>
    </Options>
  );
};

const Options = styled.div`
  position: absolute;
  top: 50px;
  left: 0;
  background-color: white;
  padding: 20px;
  width: 200px;
  z-index: 99;

  button {
    border: 0;
    width: 100%;
    font-family: SF Pro Display;
    font-style: normal;
    font-weight: normal;
    font-size: 16px;
    line-height: 141.7%;
    color: #000000;
    text-align: left;
    padding: 15px 0;
    border-bottom: 1px solid #e8e8e8;

    &:last-child {
      border-bottom: 0;
    }
  }
`;

const bookOptions = {
  display: "none",
  opacity: 0,
};

export default BookOptions;
