import React from 'react'
// import * as BooksAPI from './BooksAPI'
import './App.css'
import styled, { css } from "styled-components"
import SearchModal from './search'
import Heading from './headings'
import Header from './header'
import Book from './book'
import {getAll} from './BooksAPI'

class BooksApp extends React.Component {
  state = {
    /**
     * TODO: Instead of using this state variable to keep track of which page
     * we're on, use the URL in the browser's address bar. This will ensure that
     * users can use the browser's back and forward buttons to navigate between
     * pages, as well as provide a good URL they can bookmark and share.
     */
    showSearchPage: false,
    books: []

  }

  componentDidMount() {
    getAll().then((books) => this.setState({
      books: books
    }));
  }

  render() {

    console.log(this.state.books);

    return (
      <div className="app">
        {this.state.showSearchPage ? (
          <SearchModal show="this.state.showSearchPage"></SearchModal>
        ) : (
          <div className="list-books">
            <Header />
                <CurrentlyReading>
                  <Wrapper>
                    <Heading size="big">Currently Reading</Heading>
                    <BookshelfBooks>
                      {this.state.books.filter(book => book.shelf === "currentlyReading").map((book) =>
                        <Book book={book}></Book>
                      )
                      }
                    </BookshelfBooks>
                  </Wrapper>
                </CurrentlyReading>
                <Wishlist>
                  <Wrapper>
                    <Heading size="big">Want to Read</Heading>
                      <BookshelfBooks>
                      {this.state.books.filter(book => book.shelf === "wantToRead").map((book) =>
                        <Book book={book}></Book>
                      )
                      }
                    </BookshelfBooks>
                  </Wrapper>
                </Wishlist>
                <Read>
                  <Wrapper>
                    <Heading size="big">Read</Heading>
                      <BookshelfBooks>
                      {this.state.books.filter(book => book.shelf === "read").map((book) =>
                        <Book book={book}></Book>
                      )
                      }
                    </BookshelfBooks>
                  </Wrapper>
                </Read>
          </div>
        )}
      </div>
    )
  }
}


const Wrapper = styled.div`

    max-width:1080px;
    width:80%;
    margin:0 auto;
    padding:60px 0;

`

const CurrentlyReading = styled.div`

    background-color:#F6F3F0;

`

const Read = styled.div`

`

const Wishlist = styled.div`


`

const BookshelfBooks = styled.div`

    display:flex;
    flex-direction:row;
    margin-top:50px;
    flex-wrap:wrap;

`


export default BooksApp;
