import React from 'react'
import styled, { css } from "styled-components"
import Heading from './headings'
import Paragraph from './paragraph'
import {update} from './BooksAPI'


const Book = ({book, onUpdate}) => {
        console.log(book);

        const shelf = 'wantToRead';

        return (
            <StyledBook>
                <BookCover
                style={{backgroundImage: `url(${book.imageLinks.thumbnail})` }}>
                </BookCover>
                <Heading size="medium">
                    {book.title}
                </Heading>
                <Paragraph size="medium">{book.authors}</Paragraph>
                <MoreOptions className="moreOptions" style={moreOptions}>
                    <ArrowIcon>
                    <svg width="23" height="12" viewBox="0 0 23 12" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path fill-rule="evenodd" clip-rule="evenodd" d="M22.6509 10.2407L21.3493 11.7592L11.5001 3.31707L1.65088 11.7592L0.349297 10.2407L11.5001 0.682912L22.6509 10.2407Z" fill="white"/>
                    </svg>
                    </ArrowIcon>
                <BookOptions className="bookOptions" style={bookOptions}>
                    <Heading size="small">Move to:</Heading>
                    <button onClick={ () => {
                        console.log("Test", book)
                        update(book, 'wantToRead').then(b => onUpdate(b))
                    }}>Want to read</button>
                    <button>Read</button>
                    <button>Remove from your lists</button>
                </BookOptions>
                </MoreOptions>

            </StyledBook>
    )}

const StyledBook = styled.div`
    width:200px;
    margin-right:20px;
    position:relative;
    transition:all 0.2s ease-in; 

    &:last-child{
        margin-right:0;
    }
    &:hover{
        .moreOptions{
            display:flex !important;
            opacity:100% !important;
        }
    }


`

const BookCover = styled.div`

    width:100%;
    height: 300px;
    background-size:cover;
    background-repeat:no-repeat;


    &:hover{
        box-shadow: 3px 4px 11px rgba(0, 0, 0, 0.25);
        background-size:cover;
        background-repeat:no-repeat;
    }

`

const MoreOptions = styled.div`

    width:50px;
    height: 50px;
    background-color:#CF9E6E;
    position:absolute;
    top:125px;
    left:75px;
    display:flex;
    justify-content:center;
    align-items:center;
    transition: all 0.2s ease-in;
    cursor:pointer;

    &:hover{
        .bookOptions{
            display:block !important;
            opacity:100% !important;
        }
        svg{
            transform:rotate(180deg)
        }
    }


`


const ArrowIcon = styled.div`

    width:25px;

`
const BookOptions = styled.div`

    position:absolute;
    top:50px;
    left:0;
    background-color:white;
    padding:20px;
    width:200px;
    z-index:99;

    button{
        border:0;
        width:100%;
        font-family: SF Pro Display;
        font-style: normal;
        font-weight: normal;
        font-size: 16px;
        line-height: 141.7%;
        color: #000000;
        text-align:left;
        padding:15px 0;
        border-bottom:1px solid #E8E8E8;

        &:last-child{
            border-bottom:0
        }
    }


`
const moreOptions = {
    display: "none",
    opacity:0
};
const bookOptions = {
    display: "none",
    opacity:0
};



export default Book;
