import React from "react";
// import * as BooksAPI from './BooksAPI'
import "./App.css";
import styled, { css } from "styled-components";
import SearchModal from "./search";
import Heading from "./headings";
import Header from "./header";
import Book from "./book";
import { getAll } from "./BooksAPI";

class BooksApp extends React.Component {
  state = {
    /**
     * TODO: Instead of using this state variable to keep track of which page
     * we're on, use the URL in the browser's address bar. This will ensure that
     * users can use the browser's back and forward buttons to navigate between
     * pages, as well as provide a good URL they can bookmark and share.
     */
    showSearchPage: false,
    books: {},
    shelves: {
      currentlyReading: [],
      wantToRead: [],
      read: [],
    },
  };

  componentDidMount() {
    getAll().then((books) =>
      this.setState({
        books: books.reduce((acc, book) => {
          acc[book.id] = book;
          return acc;
        }, {}),
        shelves: {
          currentlyReading: books
            .filter((book) => book.shelf === "currentlyReading")
            .map((b) => b.id),
          wantToRead: books
            .filter((book) => book.shelf === "wantToRead")
            .map((b) => b.id),
          read: books.filter((book) => book.shelf === "read").map((b) => b.id),
        },
      })
    );
  }

  render() {
    const renderBook = (book) => {
      return (
        <Book
          book={book}
          onUpdate={(newBookOrder) => {
            this.setState({
              ...this.state,
              shelves: {
                ...newBookOrder,
              },
            });
          }}
        ></Book>
      );
    };

    return (
      <div className="app">
        {this.state.showSearchPage ? (
          <SearchModal show="this.state.showSearchPage"></SearchModal>
        ) : (
          <div className="list-books">
            <Header />
            <CurrentlyReading>
              <Wrapper>
                <Heading size="big">Currently Reading</Heading>
                <BookshelfBooks>
                  {this.state.shelves.currentlyReading.map((bookId) =>
                    renderBook(this.state.books[bookId])
                  )}
                </BookshelfBooks>
              </Wrapper>
            </CurrentlyReading>
            <Wishlist>
              <Wrapper>
                <Heading size="big">Want to Read</Heading>
                <BookshelfBooks>
                  {this.state.shelves.wantToRead.map((bookId) =>
                    renderBook(this.state.books[bookId])
                  )}
                </BookshelfBooks>
              </Wrapper>
            </Wishlist>
            <Read>
              <Wrapper>
                <Heading size="big">Read</Heading>
                <BookshelfBooks>
                  {this.state.shelves.read.map((bookId) =>
                    renderBook(this.state.books[bookId])
                  )}
                </BookshelfBooks>
              </Wrapper>
            </Read>
          </div>
        )}
      </div>
    );
  }
}

const Wrapper = styled.div`
  max-width: 1080px;
  width: 80%;
  margin: 0 auto;
  padding: 60px 0;
`;

const CurrentlyReading = styled.div`
  background-color: #f6f3f0;
`;

const Read = styled.div``;

const Wishlist = styled.div``;

const BookshelfBooks = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 50px;
  flex-wrap: wrap;
`;

export default BooksApp;
