import React from 'react'
import styled, { css } from "styled-components"
import Heading from './headings'
import Paragraph from './paragraph'

const Book = () => {

        return (
            <StyledBook>
                <BookCover
                style={{backgroundImage: 'url("http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api")' }}>
                </BookCover>
                <Heading size="medium">
                    To Kill a Mockingbird
                </Heading>
                <Paragraph size="medium">Harper Lee</Paragraph>
                <MoreOptions>
                    <ArrowIcon>

                    </ArrowIcon>
                </MoreOptions>
            </StyledBook>
    )}

const StyledBook = styled.div`
    width:200px;
    margin-right:20px;

    &:last-child{
        margin-right:0;
    }

`
const BookCover = styled.div`

    width:100%;
    height: 300px;
    background-size:cover;
    background-repeat:no-repeat;

`

const MoreOptions = styled.div`

    width:50px;
    height: 50px;
    background-color:#CF9E6E;

`
const ArrowIcon = styled.div`

    width:25px;

`



export default Book;
